<?php

declare(strict_types=1);

use Illuminate\Foundation\Testing\RefreshDatabase;
use Tests\TestCase;

uses(TestCase::class)->in('Feature', 'Unit');
uses(RefreshDatabase::class)->in('Feature', 'Unit');

beforeAll(function () {
    // Set Chapa environmental variables.
    config()->set('moneyman.providers.chapa.secret_key', 'test-secret-key');
    config()->set('moneyman.providers.chapa.callback_url', 'test-callback-url');
    config()->set('moneyman.providers.chapa.webhook_secret', 'https://domain.tld/webhook');

    // Set Santimpay environmental variables.
    config()->set('moneyman.providers.santimpay.public_key', '');
    config()->set('moneyman.providers.santimpay.private_key', '');
    config()->set('moneyman.providers.santimpay.merchant_id', 'test-merchant-id');
    config()->set('moneyman.providers.santimpay.token', 'test-token');
    config()->set('moneyman.providers.santimpay.callback_url', 'https://domain.tld/webhook');

    // Set Telebirr environmental variables.
    config()->set('moneyman.providers.telebirr.merchant_app_id', 'test-merchant-id');
    config()->set('moneyman.providers.telebirr.fabric_app_id', 'test-fabric-id');
    config()->set('moneyman.providers.telebirr.short_code', 'test-short-code');
    config()->set('moneyman.providers.telebirr.app_secret', 'test-app-secret');
    config()->set('moneyman.providers.telebirr.private_key', '');
    config()->set('moneyman.providers.telebirr.callback_url', 'https://domain.tld/webhook');
});
